import React from "react";
import { useState } from "react";
import { useHistory } from "react-router";
import {AiOutlineLike,AiFillLike} from "react-icons/ai"
import "./style.css";


const Cards = () => {
  const [arr,setArr] = useState([
    {
      id: "0",
      name: "Heart",
      img: "./heart.jpg",
      disc: "Heart Card",
      isLike: false,
    },
    {
      id: "1",
      name: "Club",
      img: "./club.png",
      disc: "Club Card",
      isLike: true,
    },
    {
      id: "2",
      name: "Spade",
      img: "./spade.png",
      disc: "Spade Card",
      isLike: false,
    },
    {
      id: "3",
      name: "Diamond",
      img: "./diamond.png",
      disc: "Diamond Card",
      isLike: false,
    },
  ]);

  const history = useHistory()

  const gitPath=(id)=>{
      history.push(`/Card/${id}`);

  } 
  const searchPath=(e)=>{
    //   console.log(e.target.search.value);
    history.push(`/Search/${e.target.search.value}`);
  }

const likeFunc=(id)=>{
    const newArr = [...arr];
    newArr[id].isCompleted = !newarr[id].isCompleted;
    setArr(newArr);

}



  return (
    <div >
   <form onSubmit={searchPath}>
            <input  type="text"  name="search" placeholder="Search... "/>
            <button>Search</button>
        </form>
      {arr.map((elem) => (
        <div >
          <img src={require(`${elem.img}`).default} alt={`card ${elem.name}`}  key={elem.id} onClick={()=>{gitPath(elem.id)}}/>
          <h2> {elem.name}  </h2>
     <div >
          <AiOutlineLike onClick={()=>{likeFunc(elem.id)}} className={elem.isLike ? 'like' : 'unlike'}/>

          </div>
        </div>
      ))}
    </div>
  );
};
export default Cards;
