import React from "react";
import { useState } from "react";
import Card from "../Card";
import heart from './heart.jpg'

import "./style.css";

const Cards = () => {
  const [arr, setArr] = useState([
    {
      id: "0",
      name: "Heart",
      img: "./heart.jpg",
      disc: "Heart Card",
    },
    {
      id: "1",
      name: "Club",
      img: "./club.jpg",
      disc: "Club Card",
    },
    {
      id: "2",
      name: "Spade",
      img: "",
      disc: "Spade Card",
    },
    {
      id: "3",
      name: "Diamond",
      img: "",
      disc: "Diamond Card",
    },
  ]);
  return (
    <div>
      {arr.map((elem) => (
        <div>
          <img src={require(`${elem.img}`).default} alt={`card ${elem.name}`} />
          <h2> {elem.name} </h2>
        </div>
      ))}
      <Card />
    </div>
  );
};
export default Cards;
