import React from "react";
import { useState } from "react";
import { useHistory } from "react-router";
import "./style.css";


const Cards = () => {
  const [arr, setArr] = useState([
    {
      id: "0",
      name: "Heart",
      img: "./heart.jpg",
      disc: "Heart Card",
    },
    {
      id: "1",
      name: "Club",
      img: "./club.png",
      disc: "Club Card",
    },
    {
      id: "2",
      name: "Spade",
      img: "./spade.png",
      disc: "Spade Card",
    },
    {
      id: "3",
      name: "Diamond",
      img: "./diamond.png",
      disc: "Diamond Card",
    },
  ]);
  const changePath=()=>{
      const change = useHistory();
  }
  return (
    <div onClick={changePath}>
      {arr.map((elem) => (
        <div>
          <img src={require(`${elem.img}`).default} alt={`card ${elem.name}`} />
          <h2> {elem.name} </h2>
        </div>
      ))}
      {/* <Card /> */}
    </div>
  );
};
export default Cards;
