import React from 'react'
import { useState } from 'react'
import Card from '../Card'

import './style.css'

 const Cards = () => {
     const [arr,setArr]=useState([
         { 
            id: "0", 
            name: "Heart",
        img: ``,
        disc:"Heart Card",
        },
        { 
            id: "1", 
            name: "Club",
        img: '',
        disc:"Club Card",
        },
        { 
            id: "2", 
            name: "Spade",
        img: '',
        disc:"Spade Card",
        },
        { 
            id: "3", 
            name: "Diamond",
        img: '',
        disc:"Diamond Card",
        }
     ]);
    return (
        <div>
            {arr.map((elem)=>
            ( 
            <div>
                <img src={elem.img} alt="card "/>
                {elem.name}

            </div>

            )
            )
        }
            <Card />


        </div>
    )
}
export default Cards
