import React from "react";
import { useState } from "react";
import "./style.css";


export const Search = () => {
    const [searchArr,setSearchArray] = useState([
        {
          id: "0",
          name: "Heart",
          img: "./heart.jpg",
          disc: "Heart Card",
        },
        {
          id: "1",
          name: "Club",
          img: "./club.png",
          disc: "Club Card",
        },
        {
          id: "2",
          name: "Spade",
          img: "./spade.png",
          disc: "Spade Card",
        },
        {
          id: "3",
          name: "Diamond",
          img: "./diamond.png",
          disc: "Diamond Card",
        },
      ]);

    const searchFunc =(e)=>{
        e.preventDefault();
    if(e.target.search.value){

        setSearchArray(searchArr.filter(elem=>
        (e.target.search.value === elem.name)))
       }
    }
    return (
        <div>
             <form onSubmit={searchFunc}>
            <input  type="text"  name="search"/>
            <button>Search</button>
        </form>
        {searchArr.map
        }

        </div>
    )
}
