import React from "react";
import { useState } from "react";
import { useParams } from "react-router";
import "./style.css";

const Card = () => {
  const id = useParams();
  const [arr] = useState([
    {
      id: "0",
      name: "Heart",
      img: "./heart.jpg",
      disc: "Heart Card",
    },
    {
      id: "1",
      name: "Club",
      img: "./club.png",
      disc: "Club Card",
    },
    {
      id: "2",
      name: "Spade",
      img: "./spade.png",
      disc: "Spade Card",
    },
    {
      id: "3",
      name: "Diamond",
      img: "./diamond.png",
      disc: "Diamond Card",
    },
  ]);
  const show=(id)=>{
   const cardArr= arr.find(elem => (elem.id == id))
       return cardArr;
  }

  return (

    <div>
        arr.find(elem => (elem.id == id));
      <img src={require(`${elem.img}`).default} alt={`card ${elem.name}`} />
      <h2> {elem.name} </h2>
    </div>
  );
};

export default Card;
