import React from "react";
import { useState } from "react";
import { useParams } from "react-router";
import "./style.css";


const Search = () => {
    const id= useParams().id;
    const [searchArr,setSearchArray] = useState([
        {
          id: "0",
          name: "Heart",
          img: "./heart.jpg",
          disc: "Heart Card",
        },
        {
          id: "1",
          name: "Club",
          img: "./club.png",
          disc: "Club Card",
        },
        {
          id: "2",
          name: "Spade",
          img: "./spade.png",
          disc: "Spade Card",
        },
        {
          id: "3",
          name: "Diamond",
          img: "./diamond.png",
          disc: "Diamond Card",
        },
      ]);

    const searchFunc =(e,id)=>{
        e.preventDefault();
    if(e.target.search.value){

        setSearchArray(searchArr.filter(elem=>
        (e.target.search.value === elem.name)))
       }
    }
    return (
        <div>
            <h1>Search </h1>
             <form onSubmit={()=>{searchFunc(id)}}>
            <input  type="text"  name="search"/>
            <button>Search</button>
        </form>
        {searchArr.map(elem=>(
        <p>{elem.name}</p>
        )
        )
        }

        </div>
    )
}
export default Search
